mapper.update:
UPDATE board 
SET readcount = readcount + 1 
WHERE num = #{num}

UPDATE board 
SET status = 2
WHERE num = #{reRef}

UPDATE board 
SET status = 1 
WHERE num = #{reRef}

UPDATE board 
SET secret = #{secret}, subject = #{subject}, content = #{content} 
WHERE num = #{num}

UPDATE board 
SET re_seq = re_seq + 1 
WHERE re_ref = #{reRef} 
AND re_seq > #{reSeq}
